import React, { Component, useState, useEffect } from 'react';
import NewUserPage from './views/NewUserPage.js';
import Nav from './components/Nav.jsx';
import { SinglePost } from './views/SinglePost.js';
import Landing from './views/Landing.js';
import Topics from './views/Topics.js';
import Dashboard from './views/Dashboard.js';
import TopicFeed from './views/TopicFeed.js';
import { Route } from "react-router-dom";
import { UserSession, AppConfig, Person } from 'blockstack';
import './App.css';

const appConfig = new AppConfig();
const session = new UserSession({ appConfig: appConfig });

const App =  () => {

  const handleSignIn = ( e ) => {
    e.preventDefault();
    userSession.redirectToSignIn( 'http://localhost:3000/newUser' );
  }

  const handleSignOut = ( e ) => {
    e.preventDefault();
    userSession.signUserOut(window.location.origin);
  }


  useEffect( () => {
    if ( session.isSignInPending() && !session.isUserSignedIn() ) {
        session.handlePendingSignIn().then( ( userData ) => {
            setData( userData );
        });
    }
    if ( session.isUserSignedIn() ) {
        setData( session.loadUserData() );
    }
  }, []);

      return (
        <div className = "App" >
          <Nav 
            session = { userSession }
            signIn = { handleSignIn } 
            signOut = { handleSignOut } 
          />
          <Route 
            path = '/newUser' 
            component = { () => <NewUserPage 
              signIn = { handleSignIn } 
              signOut = { handleSignOut } 
              session = { userSession }
              />
            }
          />
          <Route exact path='/' component={ Landing } />
          <Route path='/topics' component={ Topics } />
          <Route path='/topic' component={ TopicFeed } />
          <Route path='/dashboard' component={ Dashboard } />
          <Route path='/post' component={ SinglePost } />
        </div>
      );

  }

export default App;
